import React from "react";

class AccountBtn_searchBar extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            isLoggedIn: false,
            username: ""
        }
    }



    componentDidMount() {
        const JWToken = localStorage.getItem("JWT");
        if (JWToken !== null) {

            fetch("/findUser", {
                header: { Authorization: `Authorization ${JWToken}` }
            }).then(function (res) {
                this.setState({isLoggedIn:true,
                                username: res.json().username});
            })
        }
    }



    render() {
        return (
            <div>
                {this.state.username}
            </div>
        )
    }


}
export default AccountBtn_searchBar;
